package javaCore;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		while(i>0) {
			System.out.println(i);
			i--;
		}
		
		System.out.println("------------");
		
		int j=20;
		do{
			System.out.println(j);
			j++;
		}while(j<30); //1 loop of execution is garuntee using do while loop
		

	}

}
